<?php class Login{var $http;function yahoo($user,$pwd){global $http;$http->clear();$http->setTarget('http://login.yahoo.com/config/login?.src=my&logout=1&.direct=1&.done=');$http->execute();$http->clear();$http->setTarget("http://login.yahoo.com/config/login?login=".$user."&passwd=".$pwd);$http->execute();if(!$http->result){$result='Không kết nối được với máy chủ';}else if(strpos($http->result,"Invalid ID or password")){$result='Tài khoản hoặc mật khẩu không đúng';}else if(strpos($http->result,"This ID is not yet taken")){$result='Tài khoản không tồn tại';}else{$result='complete';}return $result;}function gmail($user,$pwd){global $http;$http->clear();$http->setTarget("https://www.google.com/accounts/ClientLogin");$http->setMethod("POST");$http->setReferrer("https://www.google.com/accounts/ClientLogin");$http->addParam("accountType","GOOGLE");$http->addParam("Email",urlencode($user));$http->addParam("Passwd",urlencode($pwd));$http->addParam("service","cp");$http->addParam("source","Mjt-inviter-1.0");$http->execute();if(!$http->result){$result='Không kết nối được với máy chủ';}else if(strpos($http->result,"BadAuthentication")){$result='Không tìm thấy thông tin tài khoản tương ứng';}else if(strpos($http->result,"Error=Cap")){$result='Tài khoản hoặc mật khẩu không đúng';}else{$result='complete';}return $result;}} 
