<?php function num_to_text($number){$number=(int)$number;if($number>0){$text='';$number=number_format($number);$num=explode(',',$number);$count=count($num);if($count==1){$dv1='trăm ';$dv2='';}else if($count==2){$dv1='nghìn ';$dv2='';}else if($count==3){$dv1='triệu ';$dv2='nghìn ';$dv3='';}else if($count==4){$dv1='tỷ ';$dv2='triệu ';$dv3='nghìn ';}else{$dv1='';$dv2='';$dv3='';}$text.=num0($num[0]);if($num[1]){$text.=$dv1;$text.=num($num[1]).$dv2;}if(isset($num[2])){$text.=num($num[2]).$dv3;}if(isset($num[3])){$text.=num($num[3]);}if(isset($num[4])){$text='Thật khó xác định khi dãy số quá lớn';}}else if($number<0){$text='Một số âm';}else{$text='Không';}return $text;}function num0($num0){$text='';$num_1=strlen($num0);if($num_1==2){$a=substr($num0,0,1);$b=substr($num0,1,1);if($a==1){$text.='mười ';if($b>0){$text.=all($b);}}else{$text.=all($a);if($b==0){$text.='mươi ';}else if($b==1){$text.='mốt ';}else{$text.=all($b);}}}else if($num_1==3){$text.=num($num0);}else{$text.=all($num0);}return $text;}function num($num){$text='';$a=substr($num,0,1);$b=substr($num,1,1);$c=substr($num,2,1);$text.=all($a).'trăm ';if($b==0){if($c>0){$text.='linh ';$text.=all($c);}}else if($b==1){$text.='mười ';if($c>0){$text.=all($c);}}else{$text.=all($b);if($c==0){$text.='mươi ';}else if($c==1){$text.='mốt ';}else{$text.=all($c);}}return $text;}function all($b){$text=$b;if($b==1){$text='một ';}else if($b==2){$text='hai ';}else if($b==3){$text='ba ';}else if($b==4){$text='bốn ';}else if($b==5){$text='năm ';}else if($b==6){$text='sáu ';}else if($b==7){$text='bảy ';}else if($b==8){$text='tám ';}else if($b==9){$text='chín ';}else{$text='không ';}return $text;} 
