<?php include($dir_index.'blog_status.php');$strLinkPager='9999/blog';$threadInPage=20;$strFilter="";if($id>0){$strFilter=" AND blg_id = ".$id;}$part_page='';$sql=$func->q("SELECT bl_id FROM tbl_blog WHERE bl_trangthai >= 0".$strFilter);$totalThread=mysql_num_rows($sql);if($totalThread>0){$totalPage=ceil($totalThread/$threadInPage);if($page>$totalPage){$page=$totalPage;}$offset=($page-1)*$threadInPage;$sql=$func->q("SELECT bl_id, bl_tieude, bl_trangthai, blg_id FROM tbl_blog WHERE bl_trangthai >= 0 ".$strFilter." ORDER BY bl_id DESC LIMIT ".$offset.", ".$threadInPage);while($row=mysql_fetch_assoc($sql)){$bl_id=$row["bl_id"];$bl_tieude=$row["bl_tieude"];$bl_trangthai=$row["bl_trangthai"];$blg_id=$row["blg_id"];$main_content.='<li><i class="fa fa-icons '.$arr_bl_trangthai[$bl_trangthai]['class'].'"></i> <a href="9999/blog_details&id='.$bl_id.'">'.$bl_tieude.'</a>';if($id==0&&$blg_id>0){$main_content.=' (<em>'.$arr_blg_id[$blg_id].'</em>)';}$main_content.='</li>';}if($totalPage>1){$part_page=$func->part_page($page,$totalPage,$strLinkPager.'&p=');}}else{$part_page='<h2 align="center">Không có dữ liệu...</h2>';}$main_content=$func->str_template($dir_admin.'template/blog.html',array('tmp.part_page'=>$part_page,'tmp.main_content'=>$main_content)); 
