<?php include($dir_index.'blog_status.php');$web_title='Blog details';$sql=$func->q("SELECT * FROM tbl_blog WHERE bl_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);if($row['bl_seo']==''){$row['bl_seo']=$func->non_mark_seo($row['bl_tieude']);}$row['bl_ngaygui']=date('d/m/Y a',$row['bl_ngaygui']);$bl_trangthai=$row['bl_trangthai'];$blg_id=$row['blg_id'];$str_trangthai='';foreach($arr_bl_trangthai as $k=>$v){if($k==$bl_trangthai){$str_trangthai.='<option value="'.$k.'" selected="selected">'.$v['ten'].'</option>';}else{$str_trangthai.='<option value="'.$k.'">'.$v['ten'].'</option>';}}$str_trangthai.='<option value="xoa">Xóa bài viết</option>';$str_blg_id='';foreach($arr_blg_id as $k=>$v){if($k==$blg_id){$str_blg_id.='<option value="'.$k.'" selected="selected">'.$v.'</option>';}else{$str_blg_id.='<option value="'.$k.'">'.$v.'</option>';}}$main_content=$func->str_template($dir_admin.'template/blog_details.html',array('tmp.id'=>$id,'tmp.bl_trangthai'=>'<select name="t_trangthai" class="w_select" onchange="if (this.value == \'xoa\') {if (confirm(\'Xóa Blog - OK để tiếp tục\') == true) {document.frm_thread_add.submit();}else{document.frm_thread_add.reset();}}">'.$str_trangthai.'</select>','tmp.blg_id'=>'<select name="t_blg_id" class="w_select">'.$str_blg_id.'</select>'));$main_content=$func->arr_tmp($row,$main_content);}else{$main_content='<h2 align="center">Không có dữ liệu...</h2>';} 
