<?php include($dir_admin.'blog_group_status.php');$sql=$func->q("SELECT * FROM tbl_blog_group WHERE blg_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$blg_ten=$row['blg_ten'];$blg_stt=$row['blg_stt'];$blg_trangthai=$row['blg_trangthai'];$str_trangthai='';foreach($arr_blg_trangthai as $k=>$v){if($k==$blg_trangthai){$str_trangthai.='<option value="'.$k.'" selected="selected">'.$v['ten'].'</option>';}else{$str_trangthai.='<option value="'.$k.'">'.$v['ten'].'</option>';}}$main_content=$func->str_template($dir_admin.'template/blog_group_edit.html',array('tmp.id'=>$id,'tmp.blg_ten'=>$blg_ten,'tmp.blg_stt'=>$blg_stt,'tmp.blg_trangthai'=>'<select name="t_trangthai" class="w_select">'.$str_trangthai.'</select>'));}else{$part_page='<h2 align="center">Không có dữ liệu...</h2>';} 
