<?php $str_page='';$strLinkPager='9999/comment';$threadInPage=28;$totalThread=$func->c("SELECT c_id FROM tbl_comment");if($totalThread>0){$arr_c_trangthai=array(-1=>'fa-facebook',0=>'fa-lock-alt',1=>'fa-unlock-alt');$totalPage=ceil($totalThread/$threadInPage);if($page>$totalPage){$page=$totalPage;}$offset=($page-1)*$threadInPage;$sql=$func->q("SELECT * FROM tbl_comment ORDER BY c_id DESC LIMIT ".$offset.", ".$threadInPage);while($row=mysql_fetch_assoc($sql)){$c_id=$row['c_id'];$c_ngaygui=$row['c_ngaygui'];$tv_id=$row['tv_id'];$p_id=$row['p_id'];$c_noidung=$row['c_noidung'];$c_trangthai=$row['c_trangthai'];$main_content.=' <tr> <td>'.$c_noidung.'</td> <td><a href="'.$func->_p_link($p_id,'comment').'" target="_blank" class="d-block">'.$p_id.'</a></td> <td><a href="9999/members_details&tv_id='.$tv_id.'" target="_blank" class="d-block">'.$tv_id.'</a></td> <td><a href="9999/members_details&tv_id='.$row['mtv_id'].'" target="_blank">'.$row['mtv_id'].'</a></td> <td class="small">'.date('d/ m/ Y (H:i)',$c_ngaygui).'</td> <td><i data-ajax="&id='.$c_id.'&status='.$c_trangthai.'&p='.$page.'" data-status="'.$c_trangthai.'" class="cur click-update-status-cmt fa fa-icons '.$arr_c_trangthai[$c_trangthai].'"></i></td> <tr>';}if($totalPage>1){$str_page=$func->part_page($page,$totalPage,$strLinkPager.'&p=');}}else{$str_page='<h1 align="center">No request...</h1>';}$main_content=$func->str_template($dir_admin.'template/comment.html',array('tmp.str_page'=>$str_page,'tmp.main_content'=>$main_content));