<?php $web_title='Danh sách khách hàng đã đặt hàng';$strFilter="";$str_hd_ngaygui='';if(isset($_GET['show_all'])){}else{$sql="SELECT hd_ngaygui FROM tbl_in_con_voi WHERE hd_trangthai != 4 AND hd_id = (select hd_id from tbl_chitiethoadon where trv_id = ".$id." order by cthd_id desc limit 0, 1) ORDER BY hd_id DESC LIMIT 0, 1";$sql=$func->q($sql);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$hd_ngaygui=(int)$row['hd_ngaygui'];if($hd_ngaygui>0){$str_hd_ngaygui=date(' (d-m-Y)',$hd_ngaygui);$hd_ngaygui=date('Y-m-d',$hd_ngaygui);$hd_ngaygui=strtotime($hd_ngaygui);$strFilter=" AND hd_ngaygui > ".$hd_ngaygui;}}}$sql=$func->q("SELECT tbl_in_con_voi.hd_id, tbl_in_con_voi.hd_ten, tbl_in_con_voi.hd_diachi, tbl_in_con_voi.hd_dienthoai, tbl_in_con_voi.hd_ngaygui, tbl_thanhvien.tv_email FROM tbl_in_con_voi, tbl_thanhvien WHERE tbl_in_con_voi.hd_trangthai != 4 ".$strFilter." AND tbl_in_con_voi.hd_id IN (select hd_id from tbl_chitiethoadon where trv_id = ".$id.") AND tbl_in_con_voi.tv_id = tbl_thanhvien.tv_id ORDER BY tbl_in_con_voi.hd_id");$totalThread=mysql_num_rows($sql);$thread_list='';$i=1;while($row=mysql_fetch_assoc($sql)){$hd_id=$row['hd_id'];$hd_ten=$row['hd_ten'];$hd_diachi=$row['hd_diachi'];$hd_dienthoai=$row['hd_dienthoai'];$hd_ngaygui=$row['hd_ngaygui'];$tv_email=$row['tv_email'];$thread_list.='<tr><td>'.$i.'</td><td>'.$hd_id.'</td><td>'.date('d-m-Y H:i:s',$hd_ngaygui).'</td><td class="capitalize-case">'.$hd_ten.'</td><td class="invoiceGiovangPhone">'.$hd_dienthoai.'</td><td class="invoiceGiovangEmail">'.$tv_email.'</td><td>'.$hd_diachi.'</td></tr>';$i++;}$main_content=$func->str_template($dir_admin.'template/invoice_giovang.html',array('tmp.str_hd_ngaygui'=>$str_hd_ngaygui,'tmp.totalThread'=>$totalThread,'tmp.thread_list'=>$thread_list)); 
