<?php $strLinkPager='9999/log_email_marketing';$threadInPage=168;$str_page='';$sql=$func->q("SELECT lpm_id FROM tbl_log_phpmailer");$totalThread=mysql_num_rows($sql);if($totalThread>0){$totalPage=ceil($totalThread/$threadInPage);if($page>$totalPage)$page=$totalPage;$offset=($page-1)*$threadInPage;$sql=$func->q("SELECT * FROM tbl_log_phpmailer ORDER BY lpm_id DESC LIMIT ".$offset.", ".$threadInPage);while($row=mysql_fetch_assoc($sql))$main_content.='<tr><td>'.$row['lpm_email'].'</td><td>'.date('d/m/Y (H:i)',$row['lpm_ngaygui']).'</td><td>'.$row['lpm_noidung'].'</td><td>'.$row['tv_id'].'</td></tr>';if($totalPage>1){$str_page=$func->part_page($page,$totalPage,$strLinkPager.'&p=');}}else{$str_page='<h2 align="center">Không có dữ liệu...</h2>';}$main_content=$func->str_template($dir_admin.'template/log_email_marketing.html',array('tmp.str_page'=>$str_page,'tmp.main_content'=>$main_content)); 
