<?php $strLinkPager='9999/log_user';$threadInPage=68;$part_page='';$sql=$func->q("SELECT lu_id FROM tbl_log_user");$totalThread=mysql_num_rows($sql);if($totalThread>0){$totalPage=ceil($totalThread/$threadInPage);if($page>$totalPage)$page=$totalPage;$offset=($page-1)*$threadInPage;$sql=$func->q("SELECT tbl_log_user.*, tbl_thanhvien.tv_email FROM tbl_log_user, tbl_thanhvien WHERE tbl_log_user.tv_id = tbl_thanhvien.tv_id ORDER BY tbl_log_user.lu_id DESC LIMIT ".$offset.", ".$threadInPage);while($row=mysql_fetch_assoc($sql)){$tv_id=$row["tv_id"];$main_content.='<tr><td><a href="9999/members_details&tv_id='.$tv_id.'">'.$row['tv_email'].'</a></td><td>'.date('d/m/Y (H:i)',$row["lu_ngay"]).'</td><td>'.$row['lu_ip'].'</td><td>'.$row['lu_noidung'].'</td></tr>';}if($totalPage>1)$part_page=$func->part_page($page,$totalPage,$strLinkPager.'&p=');}else{$part_page='<h2 align="center">Không có dữ liệu...</h2>';}$main_content=$func->str_template($dir_admin.'template/log_user.html',array('tmp.part_page'=>$part_page,'tmp.main_content'=>$main_content)); 
