<?php $tv_id=(int)$_GET['tv_id'];$strLinkPager='9999/invoice';$threadInPage=68;$p_page='';$sql=$func->q("SELECT * FROM tbl_thanhvien WHERE tv_id = ".$tv_id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$tv_email=$row['tv_email'];$tv_hoten=$row['tv_hoten'];$tv_dienthoai=$row['tv_dienthoai'];$tv_diachi=$row['tv_diachi'];$tv_ngaydangky=$row['tv_ngaydangky'];$tv_ngaydangky=date('H:i:s d/m/Y',$tv_ngaydangky);$tv_ngaydangnhap=$row['tv_ngaydangnhap'];$tv_ngaydangnhap=date('H:i:s d/m/Y',$tv_ngaydangnhap);$tv_money=$row['tv_money'];$tv_trangthai=$row['tv_trangthai'];$arr_tv_trangthai=array(0=>'KHÓA',1=>'Bình thường',2=>'Đăng ký nhanh (chờ kích hoạt email)',3=>'Nhân viên (hệ thống báo cáo công việc)',4=>'Non-fiction');$str_tv_trangthai='';foreach($arr_tv_trangthai as $k=>$v){if($k==$tv_trangthai){$str_tv_trangthai.='<option value="'.$k.'" selected="selected">'.$v.'</option>';}else{$str_tv_trangthai.='<option value="'.$k.'">'.$v.'</option>';}}$sub_str_admin='Thành viên';$sql=$func->q("SELECT * FROM tbl_admin WHERE tv_id = ".$tv_id);if(mysql_num_rows($sql)>0){$sub_str_admin='Quản trị website';}$sub_str_admin='<a title="Xem và phân quyền quản trị cho tài khoản này" href="9999/admin_add&id='.$tv_id.'&email='.$tv_email.'">'.$sub_str_admin.'</a>';$totalThread=$func->c("SELECT hd_id FROM tbl_in_con_voi WHERE tv_id = ".$tv_id);$invoice_list='';if($totalThread>0){$totalPage=ceil($totalThread/$threadInPage);if($page>$totalPage){$page=$totalPage;}$offset=($page-1)*$threadInPage;$sql=$func->q("SELECT hd_id, hd_ngaygui, hd_trangthai FROM tbl_in_con_voi WHERE tv_id = ".$tv_id." ORDER BY hd_id DESC LIMIT ".$offset.", ".$threadInPage);while($row=mysql_fetch_assoc($sql)){$hd_id=$row['hd_id'];$hd_ngaygui=$row['hd_ngaygui'];$hd_trangthai=$row['hd_trangthai'];$invoice_list.='<tr class="hd_status'.$hd_trangthai.'"><td><a href="9999/invoice_details&id='.$hd_id.'">'.$hd_id.'</a></td><td>'.date('H:i d/m/y',$hd_ngaygui).'</td><td>'.$arr_hd_trangthai[$hd_trangthai].'</td><td>&nbsp;</td><td 