<?php $sql=$func->q("SELECT * FROM tbl_a_nhomtin WHERE ant_id = ".$fid);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$ant_ten=$row['ant_ten'];$ant_seo=$row['ant_seo'];$ant_title=$row['ant_title'];$ant_meta_title=$row['ant_meta_title'];$ant_keywords=$row['ant_keywords'];$ant_news_keywords=$row['ant_news_keywords'];$ant_description=$row['ant_description'];$ant_stt=$row['ant_stt'];$ant_trangthai=$row['ant_trangthai'];$arr_trangthai=array(0=>'[ Ẩn ]',1=>'Hiển thị');$str_trangthai='';foreach($arr_trangthai as $k=>$v){if($k==$ant_trangthai){$str_trangthai.='<option value="'.$k.'" selected="selected">'.$v.'</option>';}else{$str_trangthai.='<option value="'.$k.'">'.$v.'</option>';}}$main_content=$func->str_template($dir_admin.'template/thread_group_c_details.html',array('tmp.fid'=>$fid,'tmp.ant_ten'=>$ant_ten,'tmp.ant_seo'=>$ant_seo,'tmp.ant_stt'=>$ant_stt,'tmp.ant_trangthai'=>'<select name="t_trangthai" class="w_select">'.$str_trangthai.'</select>','tmp.ant_title'=>$ant_title,'tmp.ant_meta_title'=>$ant_meta_title,'tmp.ant_keywords'=>$ant_keywords,'tmp.ant_news_keywords'=>$ant_news_keywords,'tmp.ant_description'=>$ant_description));}else{$part_page='<h2 align="center">Không có dữ liệu...</h2>';} 
