<?php if($id>0){if(isset($_GET['act_group'])&&($act_group=trim($_GET['act_group']))!=''){if($act_group=='status'){$sql=$func->q("SELECT th_trangthai FROM tbl_thuonghieu WHERE th_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);if($row['th_trangthai']==0){$th_trangthai=1;}else{$th_trangthai=0;}$func->q("UPDATE tbl_thuonghieu SET th_trangthai = ".$th_trangthai." WHERE th_id = ".$id);$func->log_admin('Update status brand #'.$id.' = '.$th_trangthai);}}else if($act_group=='order'&&isset($_GET['move'])){$sql=$func->q("SELECT th_stt FROM tbl_thuonghieu WHERE th_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$th_stt=$row['th_stt'];if($_GET['move']=='up'){$th_stt--;if($th_stt<0){$th_stt=0;}}else{$th_stt++;}$func->q("UPDATE tbl_thuonghieu SET th_stt = ".$th_stt." WHERE th_id = ".$id);$func->log_admin('Update order brand #'.$id.' = '.$th_stt);}}else if($act_group=='delete'){$func->q("UPDATE tbl_thuonghieu SET th_trangthai = -1 WHERE th_id = ".$id);$func->log_admin('Delete brand #'.$id.', <a href="9999/thuonghieu&act_group=delete_restore&id='.$id.'" target="_blank">hoàn tác</a>.');}else if($act_group=='delete_restore'){$func->q("UPDATE tbl_thuonghieu SET th_trangthai = 0 WHERE th_id = ".$id);$func->log_admin('Restore brand #'.$id);}$_SESSION['session_update_cache']=1;}}$arr_a_trangthai=array('fa-lock-alt','fa-unlock-alt');$strFilter="";$gname='';$sql=$func->q("SELECT * FROM tbl_thuonghieu WHERE th_trangthai >= 0 ORDER BY th_stt");while($row=mysql_fetch_assoc($sql)){$th_id=$row['th_id'];$th_ten=$row['th_ten'];$th_seo=$row['th_seo'];$th_stt=$row['th_stt'];$th_trangthai=$row['th_trangthai'];$main_content.=' <div class="div-inline-block"> <div> <input type="button" name="a_'.$th_id.'" value="'.$th_stt.'" class="bwg" disabled="disabled" /> </div> <div><i title="Sắp xếp" data-id="'.$th_id.'" data-move="down" class="cur click-order-th fa fa-icons fa-caret-down"></i></div> <div><i title="Sắp xếp" data-id="'.$th_id.'" data-move="up" class="cur click-order-th fa fa-icons fa-caret-up"></i></div> <div><i title="Cập nhật trạng thái" data-id="'.$th_id.'" class="cur click-status-th fa fa-icons '.$arr_a_trangthai[$th_trangthai].'"></i></div> <div><i title="Xóa '.$th_ten.'" data-id="'.$th_id.'" class="cur click-delete-th fa fa-icons fa-trash"></i></div> <div> <a title="Xem chi tiết nhóm" href="9999/thuonghieu_details&id='.$th_id.'"><i class="fa fa-edit fa-icons"></i> <strong>'.$th_ten.'</strong></a></div> <div> - <em>'.$th_seo.'</em></div> </div> ';}$main_content=$func->str_template($dir_admin.'template/thuonghieu.html',array('tmp.gname'=>$gname,'tmp.main_content'=>$main_content)); 
