<?php $sql=$func->q("SELECT * FROM tbl_thuonghieu WHERE th_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$th_ten=$row['th_ten'];$th_seo=$row['th_seo'];$th_phantram=$row['th_phantram'];$th_title=$row['th_title'];$th_meta_title=$row['th_meta_title'];$th_keywords=$row['th_keywords'];$th_news_keywords=$row['th_news_keywords'];$th_description=$row['th_description'];$th_noidung=$row['th_noidung'];$th_logo=$row['th_logo'];$th_banner=$row['th_banner'];$th_banner2=$row['th_banner2'];$th_stt=$row['th_stt'];$th_trangthai=$row['th_trangthai'];$arr_trangthai=array(0=>'[ Ẩn ]',1=>'Hiển thị');$str_trangthai='';foreach($arr_trangthai as $k=>$v){$sl='';if($k==$th_trangthai)$sl=' selected="selected"';$str_trangthai.='<option value="'.$k.'" '.$sl.'>'.$v.'</option>';}$main_content=$func->str_template($dir_admin.'template/thuonghieu_details.html',array('tmp.id'=>$id,'tmp.th_ten'=>$th_ten,'tmp.th_seo'=>$th_seo,'tmp.th_phantram'=>$th_phantram,'tmp.th_stt'=>$th_stt,'tmp.th_trangthai'=>'<select name="t_trangthai" class="w_select">'.$str_trangthai.'</select>','tmp.th_noidung'=>$th_noidung,'tmp.th_logo'=>$th_logo,'tmp.th_banner'=>$th_banner,'tmp.th_banner2'=>$th_banner2,'tmp.th_title'=>$th_title,'tmp.th_meta_title'=>$th_meta_title,'tmp.th_keywords'=>$th_keywords,'tmp.th_news_keywords'=>$th_news_keywords,'tmp.th_description'=>$th_description));}else{$part_page='<h2 align="center">Không có dữ liệu...</h2>';} 
