<?php $okey=0;$m='';if(isset($_GET['v'])&&($v=$_GET['v'])!=''){$tv_email=$func->code64($v,0);if($func->check_email_type($tv_email)==1){$sql=$func->q("SELECT tv_id, tv_money, tv_trangthai FROM tbl_thanhvien WHERE tv_email = '".$tv_email."'");if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$tv_id=$row['tv_id'];$tv_money=$row['tv_money'];$tv_trangthai=$row['tv_trangthai'];if($tv_trangthai==2&&$tv_money==0){$tv_money+=$event_discount;$sql="UPDATE tbl_thanhvien SET tv_money = ".$tv_money.", tv_trangthai = 1 WHERE tv_id = ".$tv_id;$func->q($sql);$okey=1;$func->log_user($tv_email.' kích hoạt Email (QRegister)');}else if($tv_trangthai==0){$m='Email không thể kích hoạt ngay lúc này';}else{$m='Email đã được kích hoạt hoặc không còn đủ điều kiện';}}else{$m='Email không tồn tại hoặc chưa được đăng ký';}}else{$m='Email ('.$tv_email.') không đúng định dạng';}}else{$m='Tiến trình tạm ngừng: không đủ dữ liệu xử lý';}$main_content=$func->str_template('template/g/active_email.html',array('tmp.js'=>'var active_status='.$okey.',mAlert=\''.$m.'\';','tmp.event_discount'=>number_format($event_discount),'tmp.activate_status'=>$m)); 
