<?php if(isset($_GET['e'],$_GET['c'])&&($email=trim($_GET['e']))!=''&&($captcha=trim($_GET['c']))!=''){$email=base64_decode($email);if(md5(substr(md5($email),0,3))==$captcha){if(isset($_GET['unlock'])&&$_GET['unlock']==1){$func->q("DELETE FROM tbl_blockemail WHERE bl_email = '".$email."'");$report='Kích hoạt nhận mail thành công';}else{$sql=$func->q("SELECT bl_ngay FROM tbl_blockemail WHERE bl_email = '".$email."'");if(mysql_num_rows($sql)>0){$report='Bạn đã từ chối nhận email rồi';}else{$func->q("INSERT INTO tbl_blockemail (bl_email, bl_ngay) VALUES ('".$email."', ".$date_time.")");$report='Từ chối nhận email thành công. Xin lỗi vì đã làm phiền bạn.';}}}else{$report='ERROR: #2';}}else{$report='ERROR: #1';}$main_content=$func->str_template('template/g/block_email.html',array('tmp.report'=>$report)); 
