<?php $sql=$func->q("SELECT bl_tieude, bl_noidung, blg_id FROM tbl_blog WHERE bl_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$bl_tieude=$row['bl_tieude'];$web_title=$bl_tieude;$group_go_to.='<span>'.$bl_tieude.'</span>';$bl_noidung=$row['bl_noidung'];$description=str_replace('"','&quot;',$func->short_string(strip_tags($bl_noidung),300,0));$description=$func->del_line($description);$blg_id=$row['blg_id'];$strsql=$func->q("SELECT bl_id, bl_tieude, bl_seo FROM tbl_blog WHERE blg_id = ".$blg_id." AND bl_id != ".$id." AND bl_trangthai > 0 ORDER BY bl_ngaygui DESC LIMIT 0, 10");$bai_viet_khac='';while($rows=mysql_fetch_assoc($strsql)){if($rows['bl_seo']==''){$rows['bl_seo']=$func->non_mark_seo($rows['bl_tieude']);}$bai_viet_khac.=' <li> <h2><a href="blog-'.$rows['bl_id'].'/'.$rows['bl_seo'].'.html">'.$rows['bl_tieude'].'</a></h2> </li> ';}$main_content=$func->str_template($dir_index.'template/g/blog_details.html',array('tmp.bl_tieude'=>$bl_tieude,'tmp.bai_viet_khac'=>$bai_viet_khac,'tmp.web_name'=>$web_name,'tmp.bl_noidung'=>$bl_noidung));}else{$main_content='<h2 align="center">Không có dữ liệu...</h2>';}