<?php $web_title='Xac nhan thay doi mat khau - Forgot password - ';$keywords='Quen mat khau'.$keywords;$description=$func->createDescription();if($mtv_id>0){die('<script type="text/javascript">window.location="'.$web_link.'u/canhan&t='.$func->mdnam($date_time).'&next=login";</script>');}else{if(isset($_SESSION['forgot_password'])){$main_content='Mật khẩu của bạn đã được cập nhật lúc: <strong>'.date('d-m-Y (H:i)',$_SESSION['forgot_password']).'</strong>. Thử kiểm tra Email nếu bạn không nhớ lần thay đổi này.';}else if(isset($_GET['t'],$_GET['block'],$_GET['u'],$_GET['code'])){$t=$_GET['t'];$block=$_GET['block'];$u=$_GET['u'];$code=$_GET['code'];if($t>($date_time-(24*3600))&&$func->mdnam($t)==$block&&$func->mdnam($u)==$code){$tv_matkhau=md5($date_time);$tv_matkhau=substr($tv_matkhau,0,8);$func->q("UPDATE tbl_thanhvien SET tv_matkhau='".$func->mdnam($tv_matkhau)."' WHERE tv_email='".$u."'");$_SESSION['forgot_password']=$date_time;$main_content='Mật khẩu đã được thay đổi. Mật khẩu mới là: <strong>'.$tv_matkhau.'</strong>';$headers=$func->build_mail_header($__cf_row['cf_email']);$message=$func->str_template('template/mail/forgot_password_step2.html',array('tmp.ngaygui'=>date('d-m-Y (H:i)',$date_time),'tmp.email'=>$u,'tmp.matkhau'=>$tv_matkhau));if($func->send_email($u,'Cap nhat Mat khau moi thanh cong',$message,$headers)==true){$main_content.=' hoặc kiểm tra Email để biết thêm chi tiết...';}}else{$main_content='Dữ liệu không chính xác...';}}else{$main_content='Dữ liệu không đầy đủ';}}$main_content='<div class="titleCSS">Xác nhận thay đổi mật khẩu</div><div align="center" style="padding:68px 0">'.$main_content.'</div>'; 
