<?php if(isset($_GET['p'])){$p=(int)$_GET['p'];}else if(isset($_SESSION['s_page'])&&$_SESSION['s_page']>0){$p=$_SESSION['s_page'];}else{$p=1;}$_SESSION['s_page']=$p;if(isset($_GET['raovatinfo'])){echo file_get_contents('http://raovat.info/service/email.php?p='.$p,0);}else{$strFilter="";$threadInPage=68;$offset=0;if(isset($_GET['cache_email'])&&($cache_email=trim($_GET['cache_email']))!=''){echo '<p><strong>Cache email:</strong> '.$cache_email.'</p>';$sql=$func->q("SELECT tv_id FROM tbl_thanhvien WHERE tv_email = '".$cache_email."' LIMIT 0, 1");if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$tv_id=$row['tv_id'];$strFilter="WHERE tv_id < ".$tv_id;}}else{$offset=rand(500,5000);}echo '<p><strong>Offset:</strong> '.$offset.'</p>';$sql=$func->q("SELECT tv_email FROM tbl_thanhvien ".$strFilter." GROUP BY tv_email ORDER BY tv_id DESC LIMIT ".$offset.", ".$threadInPage);$email_list='';while($row=mysql_fetch_assoc($sql)){$email_list.='<li>'.$row['tv_email'].'</li>';}echo '<ul>'.$email_list.'</ul>';} 
