<?php $color_list='<em>Chưa có màu nào? Nhấp vào [Thêm màu mới] để thêm màu khác &raquo;</em>';if($id>0){$sql=$func->q("SELECT trv_id, trv_img, trv_trangthai, trv_color FROM tbl_tinraovat WHERE trv_id = ".$id." OR color_id = ".$id." ORDER BY trv_id");if(mysql_num_rows($sql)>1){$color_list='';while($row=mysql_fetch_assoc($sql)){$trv_id=$row['trv_id'];$trv_img=$row['trv_img'];$trv_trangthai=$row['trv_trangthai'];$trv_color=$row['trv_color'];$color_list.='<li class="trv-id'.$trv_id.' trv-trangthai'.$trv_trangthai.'"><a title="'.$trv_color.'" href="9999/thread_edit&pid='.$trv_id.'"><img src="'.$trv_img.'" width="60" height="60" /></a></li>';}$color_list='<ul class="cf">'.$color_list.'</ul>';}}echo $color_list; 
