<?php $sql=$func->q("SELECT ul_link FROM tbl_upload WHERE ul_id = ".$id." AND ul_site = 0 AND tv_id = ".$mtv_id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$ul_link=$row['ul_link'];if(strstr($ul_link,'google')==true&&strstr($ul_link,'https://')==true){echo '<b>Xóa URL</b>';}else{if(strstr($ul_link,'upload.')==true){echo '<b>Xóa Sub</b>';}else{$ul_link=strstr($ul_link,'upload/');if($ul_link!=''&&file_exists($dir_index.$ul_link)){unlink($dir_index.$ul_link);}else{echo '<b>File not found</b>';}}}$func->q("DELETE FROM tbl_upload WHERE ul_id = ".$id." AND ul_site = 0 AND tv_id = ".$mtv_id);echo '!';}else{echo '<b>OH NO!</b>';} 
