<?php $tv_email=trim($_POST['t_email']);$ad_nhansu=(isset($_POST['t_nhansu']))?(int)$_POST['t_nhansu']:0;$ad_time_block=(isset($_POST['t_time_block']))?(int)$_POST['t_time_block']:0;$ad_thanhvien=(isset($_POST['t_thanhvien']))?(int)$_POST['t_thanhvien']:0;$ad_oder=(isset($_POST['t_oder']))?(int)$_POST['t_oder']:0;$ad_oder_change=(isset($_POST['t_oder_change']))?(int)$_POST['t_oder_change']:0;$ad_thread=(isset($_POST['t_thread']))?(int)$_POST['t_thread']:0;$ad_other=(isset($_POST['t_other']))?(int)$_POST['t_other']:0;$msn='';if(isset($arr_session_admin)&&$arr_session_admin['ad_thanhvien']==1){if($tv_email!=''){if($func->check_email_type($tv_email)==1){$sql=$func->q("SELECT tv_id FROM tbl_thanhvien WHERE tv_email = '".$tv_email."' LIMIT 0, 1");$row=mysql_fetch_assoc($sql);$tv_id=(int)$row['tv_id'];}else{$tv_id=(int)$tv_email;}if($tv_id>0){if($tv_id==$_gl_admin)$ad_thanhvien=1;$func->q("DELETE FROM tbl_admin WHERE tv_id = ".$tv_id);if($ad_nhansu+$ad_time_block+$ad_thanhvien+$ad_oder+$ad_oder_change+$ad_thread+$ad_other==0){$msn='XÓA tài khoản admin thành công';}else{$sql="INSERT INTO tbl_admin (tv_id, ad_nhansu, ad_time_block, ad_thanhvien, ad_oder, ad_oder_change, ad_thread, ad_other) VALUES (".$tv_id.", ".$ad_nhansu.", ".$ad_time_block.", ".$ad_thanhvien.", ".$ad_oder.", ".$ad_oder_change.", ".$ad_thread.", ".$ad_other.")";$func->q($sql);$msn='THÊM tài khoản admin thành công';}}else{$msn='Không xác định một tài khoản hợp lệ';}}else{$msn='Email hoặc ID không tồn tại';}}else{$msn='Chỉ có Administrator mới có quyền làm việc này';}$func->log_admin('Add staff: '.$tv_email);die('<script type="text/javascript">parent.update_admin_complete("'.$msn.'");</script>'); 
