<?php $func->q("INSERT INTO tbl_blog (bl_tieude, bl_ngaygui, bl_trangthai) VALUES ('New Blog', ".$date_time.", 0)");$sql=$func->q("SELECT bl_id FROM tbl_blog ORDER BY bl_id DESC LIMIT 0, 1");if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$bl_id=(int)$row['bl_id'];$func->log_admin('Add blog: #'.$bl_id);die('<script type="text/javascript">window.location="blog_details&id='.$bl_id.'";</script>');}else{$func->log_admin('ERROR: add blog');die('<script type="text/javascript">window.location="blog";</script>');} 
