<?php $ok=0;$tv_email=trim(strip_tags($_POST['t_email']));$tv_email=strtolower($tv_email);$tv_gioitinh=0;if(isset($_POST['t_gioitinh'])){$tv_gioitinh=(int)$_POST['t_gioitinh'];}if($func->check_email_type($tv_email)==1){$sql=$func->q("SELECT tv_id, tv_money, tv_trangthai FROM tbl_thanhvien WHERE tv_email = '".$tv_email."'");if(mysql_num_rows($sql)>0){$ok=2;$row=mysql_fetch_assoc($sql);$tv_id=$row['tv_id'];$tv_money=$row['tv_money'];$tv_trangthai=$row['tv_trangthai'];if($tv_trangthai==2){$m='Cảm ơn bạn. Bạn hãy xác nhận email trước khi tiếp tục';}else if($tv_money>0){$m='Tài khoản của bạn đã được nhận khuyến mại';}else{$func->q("UPDATE tbl_thanhvien SET tv_money = ".$event_discount." WHERE tv_id = ".$tv_id);$m='Chào mừng bạn quay trở lại. Thân tặng bạn '.number_format($event_discount).'đ vào tài khoản.';}}else{$tv_trangthai=2;$sql="INSERT INTO tbl_thanhvien (tv_email, tv_matkhau, tv_gioitinh, tv_ngaydangky, tv_trangthai) VALUES ('".$tv_email."', '', ".$tv_gioitinh.", ".$date_time.", ".$tv_trangthai.")";$func->q($sql);$ok=1;$uri_activete_email=$web_link;$uri_activete_email.='actions/active_email';$uri_activete_email.='&v='.$func->code64($tv_email,1);$voucher_code='';if($event_discount>0){$tieu_de_mail='Kich hoat email, nhan ma giam gia khi mua hang tai '.$web_name;}else{$tieu_de_mail='Kich hoat email '.$web_name;}$message=$func->str_template('template/mail/qregister.html',array('tmp.event_discount'=>number_format($event_discount),'tmp.web_name'=>$web_name,'tmp.voucher_code'=>$voucher_code,'tmp.uri_activete_email'=>$uri_activete_email));$m='Cảm ơn bạn! Đăng ký nhận bản tin thành công';}}else{$m=$tv_email.': Email không đúng định dạng được hỗ trợ';}die('<script type="text/javascript">parent._global_js_eb.i_func.cpl_register_email("'.$m.'", '.$ok.');</script>'); 
