<?php $p_id=(int)$_POST['t_comment_id'];$c_noidung=trim(strip_tags($_POST['comment_content']));$m='';$cpl=0;if($p_id>0&&strlen($c_noidung)>5){if($mtv_id>0){$c_trangthai=0;if(isset($_SESSION['stv_admin'])){$c_trangthai=1;}$tv_nick=explode('@',$mtv_email);$tv_nick=$tv_nick[0];$sql="INSERT INTO tbl_comment (c_noidung, c_ngaygui, c_trangthai, tv_id, tv_nick, p_id) VALUES ('".$c_noidung."', ".$date_time.", ".$c_trangthai.", ".$mtv_id.", '".$tv_nick."', ".$p_id.")";$func->q($sql);$func->q("UPDATE tbl_tinraovat SET trv_comment = trv_comment + 1 WHERE trv_id = ".$p_id);$ee=explode("\n",$c_noidung);$m='';foreach($ee as $v){$m.=trim($v).'<br />';}$cpl=1;}else{$m='Không xác định được tài khoản';}}else{$m='Nội dung bình luận quá ngắn';}die('<script type="text/javascript">parent.func_comment.cpl("'.$m.'", '.$cpl.');</script>'); 
