<?php $sql=$func->q("SELECT * FROM tbl_tinraovat WHERE trv_id = ".$id);if(mysql_num_rows($sql)>0){$row=mysql_fetch_assoc($sql);$trv_tieude=$row['trv_tieude'];$p_link=$m_web_link.$m_service_name.$id.'/';$trv_img='<img alt="'.$row['trv_tieude'].'" title="'.$row['trv_tieude'].'" src="images/bg_lazyload_140.gif" data-img="'.$row['trv_img'].'" class="overload-avt" />';$trv_giamoi=$row['trv_giamoi'];$trv_giovang=$row['trv_giovang'];$pro_giovang=0;if($trv_giovang>0){$pro_giovang=$date_time-$row['trv_giovang'];}$hoten='';$dienthoai='';$email='';$diachi='';if(isset($_SESSION['session_cart_info'])){$a=$_SESSION['session_cart_info'];$hoten=$a['hoten'];$dienthoai=$a['dienthoai'];$email=$a['email'];$diachi=$a['diachi'];}$main_content=$func->str_template('template/cart.html',array('tmp.js'=>'var cart_pro_status='.$row['trv_trangthai'].',cart_pro_soluong='.($row['trv_max_mua']-$row['trv_mua']).',cart_pro_giovang='.$pro_giovang.';','tmp.id'=>$id,'tmp.trv_tieude'=>$trv_tieude,'tmp.p_link'=>$p_link,'tmp.trv_img'=>$trv_img,'tmp.trv_giamoi'=>$trv_giamoi,'tmp.str_trv_giamoi'=>number_format($trv_giamoi),'tmp.hoten'=>$hoten,'tmp.dienthoai'=>$dienthoai,'tmp.email'=>$email,'tmp.trv_giamoi'=>$trv_giamoi,'tmp.diachi'=>$diachi));}else{$main_content='<h3>Không xác định được Sản phẩm</h3>';} 
